import styled from "styled-components";
import { Link, NavLink } from "react-router-dom";

export const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 16px;
`;

export const Header = styled.header`
  background: linear-gradient(to bottom, #000000, #333333);
  padding: 8px 0;
  border-bottom: 1px solid black;

  > div {
    display: flex;
    height: 70px;
    align-items: center;
    justify-content: space-between;
    gap: 12px;
  }

  > nav {
    display: flex;
  }
`;

export const Logo = styled(Link)`
  font-weight: 700;
  margin: 0;
  margin-left: 50px;
  text-decoration: none;
  color: black;
`;

export const Links = styled(NavLink)`
  padding: 8px 16px;
  border-radius: 4px;
  text-decoration: none;
  color: #fff;
  font-weight: 500;

  &.active {
    color: white;
    background-color: orangered;
  }
`;

export const Contacts = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

// Media Queries
const mediaQueries = {
  small: "576px",
  medium: "768px",
  large: "992px",
  extraLarge: "1200px",
};

export const responsive = {
  mobile: `(max-width: ${mediaQueries.small})`,
  tablet: `(max-width: ${mediaQueries.medium})`,
  desktop: `(max-width: ${mediaQueries.large})`,
  largeDesktop: `(max-width: ${mediaQueries.extraLarge})`,
};
export const MainContent = styled.div`
  margin: 0 auto;
  max-width: 800px;
  background-color: rgba(255, 255, 255, 0.8);
  padding: 20px;

  @media ${responsive.tablet} {
    max-width: 600px;
  }

  @media ${responsive.mobile} {
    max-width: 400px;
    padding: 10px;
  }
`;

export const Footer = styled.footer`
  background: linear-gradient(to bottom, #000000, #333333);
  color: #fff;
  padding: 20px;

  > div {
    display: flex;
    margin: auto 0;
    justify-content: space-around;

    @media ${responsive.tablet} {
      flex-direction: column;
      align-items: center;
    }
  }

  li {
    margin-top: 5px;
  }

  a {
    color: #fff;
    text-decoration: none;
  }

  ul {
    list-style-type: none;
  }
`;
